    PrintIntegers( a, NUMBER_OF_ELEMENTS );
    qsort( a, NUMBER_OF_ELEMENTS, sizeof(int), DoCompareObject );
    cout << "After sorted: \n";
    PrintIntegers( a, NUMBER_OF_ELEMENTS );
    return 0;
}
int DoCompareObject( const void * e1, const void * e2 )
{
    return CompareInteger( *(const int *)e1, *(const int *)e2 );
}
