#include <iostream>
using namespace std;
//output true or false based on the value
//title is notification
inline void test(const char *title, bool value){
    cout << title << " returns "
    << (value ? "true" : "false") << endl;
}
int main() {
    string s1 = "DEF";
    cout << "s1 is " << s1 << endl;
    string s2;
    cout << "Please enter s2: ";
    cin >> s2;
    cout << "length of s2: " << s2.length() << endl;
    test("s1 <= \"ABC\"", s1 <= "ABC");
    test("\"DEF\" <= s1", "DEF" <= s1);
    s2 += s1;
    cout << "s2 = s2 + s1: " << s2 << endl;
    cout << "length of s2: " << s2.length() << endl;
    return 0;
}
```

### getline

getline(*cin*, **string name**)

getline(*cin*, **string name**, ',')

example:

```c++
include <iostream>
#include <string>
using namespace std;
int main() {
    for (int i = 0; i < 2; i++){
    string city, state;
    getline(cin, city, ',');
    getline(cin, state);
    cout << "City:" << city <<  State:" << state << endl;
    }
    return 0;
}
```

